    postDate: '2021-07-21T10:00:00-07:00',
    slug: 'freight-yard-symphony-by-robert-abel-visual-effects-pioneer',
    title: '"Freight Yard Symphony" by Robert Abel, Visual Effects Pioneer',
    uri: 'blog/freight-yard-symphony-by-robert-abel-visual-effects-pioneer',
    image: null,
    imageCarousel: [],
